def login():
    username = entry_1.get()
    password = entry_2.get()
    with open('users.txt', 'r') as f:
        for line in f:
            if username not in line and password not in line:
                print("Please recheck what have you written?")
            elif username in line and password not in line:
                print("Please recheck what have you written?")
            elif username not in line and password in line:
                print("Please recheck what have you written?")
            elif username and password in line:
                    # Extract the part after "Names: " and strip any leading/trailing whitespace
                names = line.split("names: ")
                print("Are you: " + str(names) + " ?")
                ans = input("(Y/N) ").lower()
                if ans == "y":
                    print("Welcome " + username)
                elif ans == "n":
                    print("Sorry, acc not found")
                else:
                    print("Error!")
            else:
                print("User or Password incorrect, please signup if didn't.")

def signup():
    with open('users.txt', 'a') as f:
        user = input("Enter user: ")
        name = input("Enter name: ")
        password = input("Enter password: ")
        f.writelines("User: " + user + ", Name: " + name + ", password: " + password + "\n")
        print("signup complete!")
    


